from src.models.model_ensemble import EnsembleClassifier
from src.utils.window import compute_critical_region


def get_critical_region(model, sequence, saprot_sequence):
    if not isinstance(model, EnsembleClassifier):
        raise NotImplementedError(f"Only support ensemble model for critical region analysis.")
    return compute_critical_region(model, sequence, saprot_sequence)


if __name__ == '__main__':
    # LLPS models
    import torch
    from src.lib.llps_model import get_llps_model
    # input
    from src.utils.saprotseq import get_saprotseq

    device = torch.device("cuda" if torch.cuda.is_available() else "cpu")
    # model
    model = get_llps_model('ensemble', local_files_only=True)
    model = model.to(device)
    # input
    # sequence: str
    # saprotseq: str
    sequence = ''
    saprotseq = get_saprotseq(sequence)
    # inference
    scores = get_critical_region(model, sequence, saprotseq)
    print("Sequence length:", len(sequence))
    print("Per-residue score length:", len(scores))     # Expected: L
    print("Per-residue scores:", scores)
